import { Component, OnDestroy, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../AuthService.service';
import { Observable, Subscription, interval, observable } from 'rxjs';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit,OnDestroy {
  intSub : Subscription = new Subscription();

  customObservable = new Observable(observer  => {
    let count = 0;
    setInterval(()=>{observer.next(count); count++;},1000)
  });

  constructor(private route : Router,private authService : AuthService) { }
  LoggedIn() { this.authService.logIn()}
  LoggedOut() {this.authService.logOut()}

  gotoSrevrePage() {
    this.route.navigate(['/servers']);
  }

  ngOnInit() {
    this.intSub = this.customObservable.subscribe(count => console.log(count));
  }
  
ngOnDestroy() {
  this.intSub.unsubscribe();
}
}
<h4>Welcome to Server Manager 4.0</h4>
<p>Manage your Servers and Users.</p>
<a (click)="gotoSrevrePage()">Server Page</a>
<button class="btn btn-primary" (click)="LoggedIn()">Log In</button>
<button class="btn btn-danger" (click)="LoggedOut()">Log Out</button>
