import { Component, OnInit } from '@angular/core';

import { ServersService, servertype } from '../servers.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-edit-server',
  templateUrl: './edit-server.component.html',
  styleUrls: ['./edit-server.component.css']
})
export class EditServerComponent implements OnInit {
  server: servertype = {id : 0, name : '', status : ''};
  serverName : string | null = '';
  serverStatus : string | null  = '';
  allowEdit : number = 0;

  constructor(private serversService: ServersService,
              private activatedRoute : ActivatedRoute,) { }

  ngOnInit() {
    //this.server = this.serversService.getServer(1);
    
    this.activatedRoute.params.subscribe(param => {
     // console.log(this.activatedRoute.snapshot.queryParams['allowEdit']);
     //console.log(this.activatedRoute.snapshot.fragment === null ? '1' : '0');
      //console.log(param["id"]);
      this.server = this.serversService.getServer(+param["id"]);
      this.serverName = this.server.name;
      this.serverStatus = this.server.status;
      });
      
      this.activatedRoute.queryParams.subscribe(qp => {this.allowEdit = qp["allowEdit"]});
  }

  onUpdateServer() {
    this.serversService.updateServer(this.server.id, {name: this.serverName, status: this.serverStatus});
  }

}
<div class="form-group">
  <label for="name">Server Name</label>
  <input
    type="text"
    id="name"
    class="form-control"
    [(ngModel)]="serverName">
</div>
<div class="form-group">
  <label for="status">Server Status</label>
  <select
    id="status"
    class="form-control"
    [(ngModel)]="serverStatus">
    <option value="online">Online</option>
    <option value="offline">Offline</option>
  </select>
</div>
<button [disabled]="allowEdit==1?false:true"
  class="btn btn-primary"
  (click)="onUpdateServer()">Update Server</button>
